create table Y9_STORAGE_CAPACITY (ID varchar(38) not null comment '主键id', CAPACITYOWNERID varchar(50) comment '容量所属人id', CAPACITYOWNERNAME varchar(50) comment '容量所属人', CAPACITYSIZE bigint comment '容量长度', CREATETIME datetime(6) comment '创建时间', OPERATORID varchar(50) comment '操作人id', OPERATORNAME varchar(50) comment '操作人', REMAININGLENGTH bigint comment '剩余长度', UPDATETIME datetime(6) comment '更新时间', primary key (ID)) comment='存储容量配置表' engine=InnoDB;
create table Y9_STORAGE_DOWNLOAD_RECORD (ID varchar(38) not null comment '主键id', DOWNLOADMODE varchar(50) comment '下载模式', DOWNLOADTIME datetime(6) comment '下载时间', DOWNLOADUSERID varchar(50) comment '下载人Id', DOWNLOADUSERNAME varchar(50) comment '下载人', FILEID varchar(50) comment '文件Id', primary key (ID)) comment='文档下载记录表' engine=InnoDB;
create table Y9_STORAGE_NETWORK_FILE (ID varchar(38) not null comment '主键id', COLLECT bit comment '是否收藏', CREATETIME datetime(6) comment '创建时间', DELFLAG bit comment '软删除标记', ENCRYPTION bit comment '是否加密', FILEPASSWORD varchar(50) comment '文件密码', FILESIZE bigint comment '文件长度', FILESTOREID varchar(50) comment '文件地址Id', FILESUFFIX varchar(10) comment '文件类别', FILETYPE integer comment '文件类别', LISTTYPE varchar(255) comment '列表类别', NAME varchar(100) comment '文件夹名称', ORGID varchar(50) comment '组织id', PARENTID varchar(50) comment '父文件夹id', TABINDEX integer comment '排序字段', UPDATETIME datetime(6) comment '更新时间', USERID varchar(38) comment '创建人id', USERNAME varchar(50) comment '创建人姓名', primary key (ID)) comment='文件节点表' engine=InnoDB;
create table Y9_STORAGE_NETWORK_FILE_SHARE (ID varchar(38) not null comment '主键id', CREATE_TIME datetime(6) comment '创建时间', FILE_NODE_ID varchar(38) comment '文件节点id', FILE_OPT_TYPE varchar(38) comment '文件操作类型', PERSON_ID varchar(255) comment '分享人Id', PERSON_NAME varchar(255) comment '分享人姓名', TO_ORGUNIT_ID varchar(255) comment '被分享组织Id', TO_ORGUNIT_NAME varchar(255) comment '被分享组织名', UPDATE_TIME datetime(6) comment '更新时间', primary key (ID)) comment='文件夹分享表' engine=InnoDB;
create table Y9_STORAGE_NETWORK_FILECOLLECT (ID varchar(38) not null comment '主键id', COLLECTROUTE varchar(1000) comment '收藏路径', COLLECTTIME datetime(6) comment '收藏时间', COLLECTUSERID varchar(50) comment '收藏人Id', FILEID varchar(50) comment '文件Id', FILENAME varchar(200) comment '文件名称', LISTNAME varchar(50) comment '列表名称', PARENTID varchar(50) comment '文件父Id', primary key (ID)) comment='文件收藏记录表' engine=InnoDB;
create index CAPACITYOWNERID_INDEX on Y9_STORAGE_CAPACITY (CAPACITYOWNERID);
create index FILE_FILEID_INDEX on Y9_STORAGE_DOWNLOAD_RECORD (FILEID);
create index FILE_USERID_INDEX on Y9_STORAGE_NETWORK_FILE (USERID);
create index TO_ORGUNIT_ID_INDEX on Y9_STORAGE_NETWORK_FILE_SHARE (TO_ORGUNIT_ID);
create index PERSON_ID_INDEX on Y9_STORAGE_NETWORK_FILE_SHARE (PERSON_ID);
create index FILE_FILEID_INDEX on Y9_STORAGE_NETWORK_FILECOLLECT (FILEID);
